CREATE TABLE STUDENT(ID BIGINT AUTO_INCREMENT PRIMARY KEY, NAME VARCHAR, BIRTHDAY DATE, "GROUP" INT);
CREATE TABLE SUBJECT(ID BIGINT AUTO_INCREMENT PRIMARY KEY, NAME VARCHAR, DESCRIPTION VARCHAR, GRADE INT);
CREATE TABLE PAYMENTTYPE(ID BIGINT AUTO_INCREMENT PRIMARY KEY, NAME VARCHAR);
CREATE TABLE PAYMENT(ID BIGINT AUTO_INCREMENT PRIMARY KEY, TYPE_ID BIGINT, AMOUNT DECIMAL, STUDENT_ID BIGINT, PAYMENT_DATE DATETIME, CONSTRAINT FK_PAYMENT_PAYMENTTYPE FOREIGN KEY (TYPE_ID) REFERENCES PAYMENTTYPE, CONSTRAINT FK_PAYMENT_STUDENT FOREIGN KEY (STUDENT_ID) REFERENCES STUDENT);
CREATE TABLE MARK(ID BIGINT AUTO_INCREMENT PRIMARY KEY, STUDENT_ID BIGINT, SUBJECT_ID BIGINT, MARK INT, CONSTRAINT FK_MARK_STUDENT FOREIGN KEY (STUDENT_ID) REFERENCES STUDENT, CONSTRAINT FK_MARK_SUBJECT FOREIGN KEY (SUBJECT_ID) REFERENCES SUBJECT);